#!/bin/bash

set -e

# ============ CONFIG ============
NETWORK="${NETWORK:-sepolia}"
ADMIN="${ADMIN:-0x0}"
MERKLE_ROOT="${MERKLE_ROOT:-0x0}"
DEADLINE_BLOCK="${DEADLINE_BLOCK:-10000000}"  # ~3 months from now
MAX_CLAIM_LOW="${MAX_CLAIM_LOW:-1000000000000000000000}"  # 1000 tokens (18 decimals)
MAX_CLAIM_HIGH="${MAX_CLAIM_HIGH:-0}"

# ============ VALIDATION ============
if [ "$ADMIN" = "0x0" ] || [ "$MERKLE_ROOT" = "0x0" ]; then
  echo "❌ Error: ADMIN and MERKLE_ROOT must be set"
  echo "Usage: ADMIN=0x... MERKLE_ROOT=0x... ./scripts/deploy.sh"
  exit 1
fi

# ============ STEP 1: Declare Contracts ============
echo "📦 Declaring MigrationToken..."
TOKEN_OUTPUT=$(sncast declare --contract-name MigrationToken --network $NETWORK 2>&1)
TOKEN_CLASS=$(echo "$TOKEN_OUTPUT" | grep "class_hash:" | sed 's/.*class_hash: //' | awk '{print $1}')

if [ -z "$TOKEN_CLASS" ]; then
  echo "❌ Failed to declare MigrationToken"
  echo "$TOKEN_OUTPUT"
  exit 1
fi

echo "✅ Token class hash: $TOKEN_CLASS"

echo "📦 Declaring MigrationPortal..."
PORTAL_OUTPUT=$(sncast declare --contract-name MigrationPortal --network $NETWORK 2>&1)
PORTAL_CLASS=$(echo "$PORTAL_OUTPUT" | grep "class_hash:" | sed 's/.*class_hash: //' | awk '{print $1}')

if [ -z "$PORTAL_CLASS" ]; then
  echo "❌ Failed to declare MigrationPortal"
  echo "$PORTAL_OUTPUT"
  exit 1
fi

echo "✅ Portal class hash: $PORTAL_CLASS"

# ============ STEP 2: Deploy Token ============
echo ""
echo "🚀 Deploying Token..."
# Args: name, symbol, admin, minter (zero = set later)
TOKEN_DEPLOY_OUTPUT=$(sncast deploy --class-hash $TOKEN_CLASS \
    --constructor-calldata str:MigToken str:MIG $ADMIN 0x0 \
    --network $NETWORK 2>&1)
TOKEN_ADDR=$(echo "$TOKEN_DEPLOY_OUTPUT" | grep "contract_address:" | sed 's/.*contract_address: //' | awk '{print $1}')

if [ -z "$TOKEN_ADDR" ]; then
  echo "❌ Failed to deploy token"
  echo "$TOKEN_DEPLOY_OUTPUT"
  exit 1
fi

echo "✅ Token deployed at: $TOKEN_ADDR"

# ============ STEP 3: Deploy Portal ============
echo ""
echo "🚀 Deploying Portal..."
# Args: owner, token, merkle_root, deadline, max_amount (u256 = low,high)
PORTAL_DEPLOY_OUTPUT=$(sncast deploy --class-hash $PORTAL_CLASS \
    --constructor-calldata $ADMIN $TOKEN_ADDR $MERKLE_ROOT \
        $DEADLINE_BLOCK $MAX_CLAIM_LOW $MAX_CLAIM_HIGH \
    --network $NETWORK 2>&1)
PORTAL_ADDR=$(echo "$PORTAL_DEPLOY_OUTPUT" | grep "contract_address:" | sed 's/.*contract_address: //' | awk '{print $1}')

if [ -z "$PORTAL_ADDR" ]; then
  echo "❌ Failed to deploy portal"
  echo "$PORTAL_DEPLOY_OUTPUT"
  exit 1
fi

echo "✅ Portal deployed at: $PORTAL_ADDR"

# ============ STEP 4: Grant Minter Role ============
echo ""
echo "🔐 Granting minter role to portal..."
sncast invoke --contract-address $TOKEN_ADDR \
    --function grant_minter_role \
    --calldata $PORTAL_ADDR \
    --network $NETWORK

echo "✅ Minter role granted"

# ============ OUTPUT ============
